    public static function get_svg( $group, $icon ) {
        $arr = array();

        switch ($group) {
            case 'site':
                $arr = self::$site;
                break;
            case 'social':
                $arr = self::$social;
                break;
            case 'additional':
                $arr = self::$additional;
                break;
        }

        return $arr[$icon];
    }

